import base32 from '../src';

describe('Encoding', function(){
    it('should encode strings', function(){
        const str1 = 'testing';
        const enc1 = 'ORSXG5DJNZTQ====';
        const str2 = 'Man is distinguished, not only by his reason, but by this singular passion from other animals, which is a lust of the mind, that by a perseverance of delight in the continued and indefatigable generation of knowledge, exceeds the short vehemence of any carnal pleasure.';
        const enc2 = '';
        const str3 = 'The difference between these three kinds of Commonwealth consisteth not in the difference of power, but in the difference of convenience or aptitude to produce the peace and security of the people; for which end they were instituted. And to compare monarchy with the other two, we may observe: first, that whosoever beareth the person of the people, or is one of that assembly that bears it, beareth also his own natural person. And though he be careful in his politic person to procure the common interest, yet he is more, or no less, careful to procure the private good of himself, his family, kindred and friends; and for the most part, if the public interest chance to cross the private, he prefers the private: for the passions of men are commonly more potent than their reason. From whence it follows that where the public and private interest are most closely united, there is the public most advanced. Now in monarchy the private interest is the same with the public. The riches, power, and honour of a monarch arise only from the riches, strength, and reputation of his subjects. For no king can be rich, nor glorious, nor secure, whose subjects are either poor, or contemptible, or too weak through want, or dissension, to maintain a war against their enemies; whereas in a democracy, or aristocracy, the public prosperity confers not so much to the private fortune of one that is corrupt, or ambitious, as doth many times a perfidious advice, a treacherous action, or a civil war.';
        const enc3 = '';
        expect(base32.encode(str1)).toEqual(enc1);
        expect(base32.encode(str2)).toEqual(enc2);
        expect(base32.encode(str3)).toEqual(enc3);
    });

    it('should encode binary data', function(){
        const buf1 = Buffer.from([0xFF, 0xFF, 0xFF, 0xFF]);
        const enc1 = '777777Y=';
        const buf2 = new Buffer('this is a t√©st');
        const enc2 = 'ORUGS4ZANFZSAYJAOTB2S43U';
        const buf3 = new Buffer('7468697320697320612074c3a97374', 'hex');
        const buf4 = new Buffer('Now, in reality, the world have paid too great a compliment to critics, and have imagined them men of much greater profundity than they really are. From this complaisance the critics have been emboldened to assume a dictatorial power, and have so far succeeded that they are now become the masters, and have the assurance to give laws to those authors from whose predecessors they originally received them.');
        const enc4 = '';
        expect(base32.encode(buf1)).toEqual(enc1);
        expect(base32.encode(buf2)).toEqual(enc2);
        expect(base32.encode(buf3)).toEqual(enc2);
        expect(base32.encode(buf4)).toEqual(enc4);
    });

});